INSERT INTO `tbl_sanpham` (`sanpham_id`, `category_id`, `brand_id_lv2`, `brand_id`, `sanpham_name`, `sanpham_chitiet`, `sanpham_mota`, `sanpham_gia`, `sanpham_giakhuyenmai`, `sanpham_soluong`, `hinh`) VALUES
(1, 1, '21', 2, 'Gia Bảo - Đường Phèn Hộp - Tiết kiệm', '<p>- Nước yến giúp ăn ngon, ngủ ngon,hồi phục sức khỏe nên được doanh nghiệp TIẾN THỊNH PHÁT cho ra đời dòng sản phẩm 6 hũ yến sào nguyên chất hạt chia Yến sào Gia Bảo-Đường Phèn, dinh dưỡng với công dụng tuyệt vời.</p>\n<br>\n<p>Thương hiệu  : Gia Bảo </p><hr>  <p>Sản xuất tại  : Việt Nam</p><hr> <p>Loại sản phẩm :\n  Yến sào</p><hr> <p>Bổ sung : Hạt chia</p><hr> <p>Thành phần    Yến Tươi: 30% ,  Nước tinh khiết: 68%, Đường phèn: 10%, Tảo biển: 2%, Lượng đường : ít đường.</p><hr>\n<p>Thể tích : 420 ml</p><hr>\n<p>Đóng gói  : Hộp.</p><hr>\n<p>Số lượng  : Hộp 6 hũ </p><hr>\n<p class=\"text-danger\">Lưu ý : Không sử dụng sản phẩm khi hết hạn ,nên sử dụng một lần khi mở nắp.</p>\n<br>\n', '<h2 class=\"text-danger\">2 -Cung cấp dinh dưỡng dành cho người bị tiểu đường</h2><br> <p>- Hiện nay trên thị trường có khá nhiều dòng sản phẩm nước yến trong đó phân chia làm hai nhóm chính: nước yến có bổ sung đường và nước yến không đường.</p><br> <p>- Nước yến không đường không chất béo được khuyên dùng cho bệnh nhân tiểu đường bởi chúng giúp kiểm soát lượng đường trong máu, cũng như cải thiện tình trạng bệnh đáng kể.</p><br> <p>Hộp 6 hũ tổ yến chưng sẵn hạt chia HERANEST không đường 420 ml được sản xuất,nhằm giúp người tiểu đường tăng cường sức khỏe,bồi bổ khí huyết và nâng cao sức đề kháng.</p><br> <p>TIẾN THỊNH PHÁT group chuyên sản xuất kinh doanh ngành hàng thực phẩm dinh dưỡng , đặt biệt là mặt hàng nước yến sào HERANEST, Gia Bảo, SALANESST,là sản phẩm phù hợp cho mọi đối tượng, giúp tăng cường sức khỏe,giảm mệt mỏi,chống lão hóa,tăng sức đề kháng.Với phương pháp chế biến cổ truyền trên dây chuyền đạt chuẩn an toàn thực phẩm,chora sản phẩm chất lượng cao.</p>', 239000, 230000, 20, 'yen-sao-gia-bao-duong-phen-10.webp'),
